	# The function which find correct serial port
	def comPortNumber(self, vid_pid, port=""):
		print(vid_pid.values())
		for p in list(serPort.comports()):
			for i in vid_pid.values():
				if port == "":
					if i in p.hwid:
						return p.device
				else:
					if i and port in p.hwid:
						return p.device
					
	
